package com.algo.array;/** * Created by sanjay.nayak on 6/4/17. */public class KthElementInTwoSortedArray {    public static void main(String args[]){        int a[] = {2,4,6,8,9};        int b[] = {3,5,7,10};        int k =5;                findKthElementInMergedArray(a,b,k);    }    private static int findKthElementInMergedArray(int[] a, int[] b, int k) {        int aLength = a.length;        int bLength = b.length;        int result = -1;        int loopCount = aLength>bLength?aLength:bLength;        int j=0;        for (int i=0;i<loopCount;){            if(i==k)                return result;            if(a[i]<b[j]){                result = a[i];                i++;            }else{                result = b[j];                j++;            }        }        return result;    }}